import{_ as e,c,o as i,a as l}from"./app.b24c6e2d.js";const g=JSON.parse('{"title":"2. Eclipse 開發工具的介紹與使用","description":"","frontmatter":{"title":"2. Eclipse 開發工具的介紹與使用"},"headers":[{"level":2,"title":"下載 Eclipse zip","slug":"下載-eclipse-zip","link":"#下載-eclipse-zip","children":[]},{"level":2,"title":"Workspace","slug":"workspace","link":"#workspace","children":[]},{"level":2,"title":"設定 Source","slug":"設定-source","link":"#設定-source","children":[]},{"level":2,"title":"設定編碼","slug":"設定編碼","link":"#設定編碼","children":[]},{"level":2,"title":"產生 Java Project","slug":"產生-java-project","link":"#產生-java-project","children":[]},{"level":2,"title":"確認使用正確的 JavaSE xx 版本，按下 Finish","slug":"確認使用正確的-javase-xx-版本-按下-finish","link":"#確認使用正確的-javase-xx-版本-按下-finish","children":[]},{"level":2,"title":"新增 class","slug":"新增-class","link":"#新增-class","children":[]},{"level":2,"title":"執行 Java 程式","slug":"執行-java-程式","link":"#執行-java-程式","children":[]},{"level":2,"title":"Perspective","slug":"perspective","link":"#perspective","children":[]},{"level":2,"title":"重設 Perspective 視窗排列","slug":"重設-perspective-視窗排列","link":"#重設-perspective-視窗排列","children":[]},{"level":2,"title":"Java Perspective","slug":"java-perspective","link":"#java-perspective","children":[]},{"level":2,"title":"Java Compile","slug":"java-compile","link":"#java-compile","children":[]},{"level":2,"title":"Java 編輯字型修改","slug":"java-編輯字型修改","link":"#java-編輯字型修改","children":[]},{"level":2,"title":"Console 字型修改","slug":"console-字型修改","link":"#console-字型修改","children":[]},{"level":2,"title":"『重要』Auto Completion","slug":"『重要』auto-completion","link":"#『重要』auto-completion","children":[]}],"relativePath":"guide/edu/chapter_2.md"}'),a={name:"guide/edu/chapter_2.md"},o=l('<h1 id="_2-eclipse-開發工具的介紹與使用" tabindex="-1">2. Eclipse 開發工具的介紹與使用 <a class="header-anchor" href="#_2-eclipse-開發工具的介紹與使用" aria-hidden="true">#</a></h1><h2 id="下載-eclipse-zip" tabindex="-1">下載 Eclipse zip <a class="header-anchor" href="#下載-eclipse-zip" aria-hidden="true">#</a></h2><ul><li><a href="http://www.eclipse.org/downloads/eclipse-packages/" target="_blank" rel="noreferrer">http://www.eclipse.org/downloads/eclipse-packages/</a><ul><li>下載 <code>Eclipse IDE for Java Developers</code></li><li>如果後面課程有教授 <code>Servlet</code> 、 <code>Jsp</code> 才下載 <code>Java EE Developers</code></li><li>解壓縮至 <code>c:\\eclipse</code></li><li>[註]課堂我們使用 <code>OpenJDK8</code> 跟 2020-06 版本。如果要裝最近的 <code>Eclipse</code>，請參考附錄2</li></ul></li></ul><h2 id="workspace" tabindex="-1">Workspace <a class="header-anchor" href="#workspace" aria-hidden="true">#</a></h2><ul><li>一個 <code>workspace</code> 為一個實體目錄。可以建置多個專案。</li><li>相關的 Java 專案可以放在同一個 <code>workspace</code> 中。</li><li>左下角的 Use this as default xxx，不要勾，方便切換至其他目錄。</li></ul><h2 id="設定-source" tabindex="-1">設定 Source <a class="header-anchor" href="#設定-source" aria-hidden="true">#</a></h2><ul><li>開發過程中我們會需要快速查詢使用的 <code>JDK</code> 程式，可以在 <code>eclipse</code> 中設定 <code>Source</code> 來源，方便切換至 <code>JDK</code> 的原始碼。</li><li><code>JDK</code> 安裝中有附 <code>src.zip</code>，包含了『 <code>java 原始碼</code> 』及 <code>javadoc</code>。</li><li>預設位置 <code>C:\\Program Files\\JDK8\\src.zip</code></li></ul><h2 id="設定編碼" tabindex="-1">設定編碼 <a class="header-anchor" href="#設定編碼" aria-hidden="true">#</a></h2><p><code>Preferences</code> &gt; <code>General</code> &gt; <code>Workspace</code></p><p><code>Text file encoding</code> 選 <code>Other</code> <code>UTF-8</code></p><h2 id="產生-java-project" tabindex="-1">產生 Java Project <a class="header-anchor" href="#產生-java-project" aria-hidden="true">#</a></h2><p><code>File</code> &gt; <code>New</code> &gt; <code>Java Project</code></p><h2 id="確認使用正確的-javase-xx-版本-按下-finish" tabindex="-1">確認使用正確的 JavaSE xx 版本，按下 Finish <a class="header-anchor" href="#確認使用正確的-javase-xx-版本-按下-finish" aria-hidden="true">#</a></h2><ul><li><code>Create a Java Project</code></li><li>輸入 <code>Project name</code></li><li><code>JRE</code> 選擇 Use an execution environment JRE: <code>JavaSE-1.8</code></li></ul><h2 id="新增-class" tabindex="-1">新增 class <a class="header-anchor" href="#新增-class" aria-hidden="true">#</a></h2><ul><li>找到 <code>New Java Class</code> 按鈕</li><li>輸入 <code>class Name</code></li><li>勾選 <code>Public static void main(String[] args)</code>，產生 <code>main</code> 方法</li></ul><h2 id="執行-java-程式" tabindex="-1">執行 Java 程式 <a class="header-anchor" href="#執行-java-程式" aria-hidden="true">#</a></h2><ul><li>程式檔案上 <code>按右鍵</code> &gt; <code>Run As</code> &gt; <code>Java Application</code>。</li><li>Toolbar 上按 <code>執行鍵</code>，選擇要執行的應用程式。</li></ul><h2 id="perspective" tabindex="-1">Perspective <a class="header-anchor" href="#perspective" aria-hidden="true">#</a></h2><ul><li>開發過程中可以透過不同『 <code>視角</code> 』來檢視檔案，常用的有兩個 <ul><li><code>Java perspective</code></li><li><code>Debug perspective</code></li></ul></li></ul><h2 id="重設-perspective-視窗排列" tabindex="-1">重設 Perspective 視窗排列 <a class="header-anchor" href="#重設-perspective-視窗排列" aria-hidden="true">#</a></h2><ul><li>每一個 <code>Perspective</code> 會有自己的視窗棑列</li><li>可以自行移除或者重新編排 <code>Perspective</code> 中的視窗</li><li>如果編排過程中有問題，可以 <code>Reset</code> 回復原始設定集值</li></ul><h2 id="java-perspective" tabindex="-1">Java Perspective <a class="header-anchor" href="#java-perspective" aria-hidden="true">#</a></h2><ul><li>主要用來編輯Java程式</li><li><code>alt + /</code> 可以用來輔助程式開發的提示， <ul><li>例如 main (<code>alt + /</code>)可以帶出main方法</li><li>sysout 可以帶出 <code>System.out.println()</code></li></ul></li><li><code>ctrl + /</code> 註解該行程式</li><li><code>alt + 上 下</code>，搬動程式上下移動</li><li><code>ctrl + alt + 下</code> 複製該行程式</li><li>執行main方法，<code>ctrl + F11</code></li></ul><h2 id="java-compile" tabindex="-1">Java Compile <a class="header-anchor" href="#java-compile" aria-hidden="true">#</a></h2><ul><li><code>eclipse</code> 會自動 compile java 程式，class 檔 預設會放在專案下的 <code>bin 目錄</code>。 <ul><li>例如 <code>bin/HelloWorld.class</code></li></ul></li><li>可以利用專案偏好設定修改輸出位置，位於 <code>Java Build Path</code>。</li><li>匯出檔案：<code>File</code> &gt; <code>expert</code> &gt; <code>選擇要匯出的檔案 (preferences)</code> &gt; <code>選擇資料夾</code></li></ul><h2 id="java-編輯字型修改" tabindex="-1">Java 編輯字型修改 <a class="header-anchor" href="#java-編輯字型修改" aria-hidden="true">#</a></h2><ul><li>系統偏好設定中可以設定字型</li><li>編輯區文字大小設定，輸入 <code>font</code>，在 <code>Java/ java Editor Text Font</code></li><li><code>Window</code> &gt; <code>Preferences</code> &gt; 搜尋 <code>font</code> &gt; <code>Colors and Fonts</code> &gt; <code>Java</code> &gt; <code>java Editor Text Font</code></li></ul><h2 id="console-字型修改" tabindex="-1">Console 字型修改 <a class="header-anchor" href="#console-字型修改" aria-hidden="true">#</a></h2><ul><li>系統偏好設定中可以設定字型</li><li>編輯區文字大小設定，輸入 <code>font</code>，在 <code>Debug/ Console font</code></li><li><code>Window</code> &gt; <code>Preferences</code> &gt; 搜尋 <code>font</code> &gt; <code>Colors and Fonts</code> &gt; <code>Debug</code> &gt; <code>Console font</code></li></ul><h2 id="『重要』auto-completion" tabindex="-1">『重要』Auto Completion <a class="header-anchor" href="#『重要』auto-completion" aria-hidden="true">#</a></h2><ul><li><code>Eclipse</code> 會幫助編輯程式，自動帶出符合的程式，稱為 <code>Auto Completion</code></li><li><code>Windows</code> &gt; <code>Preferences</code> &gt; <code>Java</code> &gt; <code>Editor</code> &gt; <code>Content Assist</code> 中設定以下字觸發自動完成 <ul><li>在 Auto Activation <ul><li>勾選 Enable auto activation</li><li>在 Auto activation triggers for Java 輸入 <code>.abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ_</code></li></ul></li></ul></li></ul>',32),d=[o];function t(r,s,n,h,p,u){return i(),c("div",null,d)}const f=e(a,[["render",t]]);export{g as __pageData,f as default};
